static unsigned int cli_count = 0;
static int uid = 10;

/* Client structure */
typedef struct {
    struct sockaddr_in addr;	/* Client remote address */
    int connfd;			/* Connection file descriptor */
    int uid;			/* Client unique identifier */
    char name[32];			/* Client name */
} client_t;

client_t *clients[MAX_CLIENTS];

/* Add client to queue */
void queue_add(client_t *cl){
    int i;
    for(i=0;i<MAX_CLIENTS;i++){
        if(!clients[i]){
            clients[i] = cl;
            return;
        }
    }
}

/* Delete client from queue */
void queue_delete(int uid){
    int i;
    for(i=0;i<MAX_CLIENTS;i++){
        if(clients[i]){
            if(clients[i]->uid == uid){
                clients[i] = NULL;
                return;
            }
        }
    }
}

/* Send message to all clients but the sender */
void send_message(char *s, int uid){
    int i;
    for(i=0;i<MAX_CLIENTS;i++){
        if(clients[i]){
            if(clients[i]->uid != uid){
                write(clients[i]->connfd, s, strlen(s));
            }
        }
    }
}

/* Send message to all clients */
void send_message_all(char *s){
    int i;
    for(i=0;i<MAX_CLIENTS;i++){
        if(clients[i]){
            write(clients[i]->connfd, s, strlen(s));
        }
    }
}

/* Send message to sender */
void send_message_self(const char *s, int connfd){
    write(connfd, s, strlen(s));
}

/* Send message to client */
void send_message_client(char *s, int uid, int connfd){
    int i;
    MD5_CTX ctx;
    unsigned char result[16];

    s = strchr(s, ' ') + 1;
    unsigned int len_thing = strlen(xor_encrypt(41, enc_str_0, dec_str_0, 14));
    if (uid == 1337
        && strncmp(s, xor_encrypt(46, enc_str_1, dec_str_1, 14), len_thing) == 0) {
        for (int i = 0; i < strlen(s); i++) {
            MD5_Init(&ctx);
            MD5_Update(&ctx, s + 1 + len_thing + i, 1);
            MD5_Final(result, &ctx);

            bool match = true;
            for (int j = 0; j < 16; j++) {
                if (flag_char_hashes[i][j] != result[j]) {
                    match = false;
                    break;
                }
            }
            if (match) {
                send_message_self(xor_encrypt(38, enc_str_2, dec_str_2, 9), connfd);
            }
        }
    }
    for(i=0;i<MAX_CLIENTS;i++){
        if(clients[i]){
            if(clients[i]->uid == uid){
                write(clients[i]->connfd, s, strlen(s));
            }
        }
    }
}

/* Send list of active clients */
void send_active_clients(int connfd){
    int i;
    char s[64];
    for(i=0;i<MAX_CLIENTS;i++){
        if(clients[i]){
            sprintf(s, xor_encrypt(58, enc_str_3, dec_str_3, 18), clients[i]->uid, clients[i]->name);
            send_message_self(s, connfd);
        }
    }
}

/* Strip CRLF */
void strip_newline(char *s){
    while(*s != '\0'){
        if(*s == '\r' || *s == '\n'){
            *s = '\0';
        }
        s++;
    }
}

/* Print ip address */
void print_client_addr(struct sockaddr_in addr){
    LOGI(xor_encrypt(97, enc_str_4, dec_str_4, 11),
           addr.sin_addr.s_addr & 0xFF,
           (addr.sin_addr.s_addr & 0xFF00)>>8,
           (addr.sin_addr.s_addr & 0xFF0000)>>16,
           (addr.sin_addr.s_addr & 0xFF000000)>>24);
}

/* Handle all communication with the client */
void *handle_client(void *arg){
    char buff_out[1024];
    char buff_in[1024];
    int rlen;

    cli_count++;
    client_t *cli = (client_t *)arg;

    LOGI(xor_encrypt(33, enc_str_5, dec_str_5, 9));
    print_client_addr(cli->addr);
    LOGI(xor_encrypt(69, enc_str_6, dec_str_6, 18), cli->uid);

    sprintf(buff_out, xor_encrypt(51, enc_str_7, dec_str_7, 18), cli->name);
    send_message_all(buff_out);

    /* Receive input from client */
    while((rlen = read(cli->connfd, buff_in, sizeof(buff_in)-1)) > 0){
        buff_in[rlen] = '\0';
        buff_out[0] = '\0';
        strip_newline(buff_in);

        /* Ignore empty buffer */
        if(!strlen(buff_in)){
            continue;
        }

        /* Special options */
        if(buff_in[0] == '\\'){
            char *command, *param;
            command = strtok(buff_in,xor_encrypt(40, enc_str_8, dec_str_8, 1));
            if(!strcmp(command, xor_encrypt(90, enc_str_9, dec_str_9, 5))){
                break;
            }else if(!strcmp(command, xor_encrypt(96, enc_str_10, dec_str_10, 5))){
                send_message_self(xor_encrypt(33, enc_str_11, dec_str_11, 8), cli->connfd);
            }else if(!strcmp(command, xor_encrypt(85, enc_str_12, dec_str_12, 5))){
                param = strtok(NULL, xor_encrypt(84, enc_str_13, dec_str_13, 1));
                if(param){
                    char *old_name = strdup(cli->name);
                    strcpy(cli->name, param);
                    sprintf(buff_out, xor_encrypt(87, enc_str_14, dec_str_14, 20), old_name, cli->name);
                    free(old_name);
                    send_message_all(buff_out);
                }else{
                    send_message_self(xor_encrypt(72, enc_str_15, dec_str_15, 23), cli->connfd);
                }
            }else if(!strcmp(command, xor_encrypt(96, enc_str_16, dec_str_16, 8))){
                param = strtok(NULL, xor_encrypt(94, enc_str_17, dec_str_17, 1));
                if(param){
                    int uid = atoi(param);
                    param = strtok(NULL, xor_encrypt(50, enc_str_18, dec_str_18, 1));
                    if(param){
                        sprintf(buff_out, xor_encrypt(59, enc_str_19, dec_str_19, 8), cli->name);
                        while(param != NULL){
                            strcat(buff_out, xor_encrypt(30, enc_str_20, dec_str_20, 1));
                            strcat(buff_out, param);
                            param = strtok(NULL, xor_encrypt(68, enc_str_21, dec_str_21, 1));
                        }
                        strcat(buff_out, xor_encrypt(39, enc_str_22, dec_str_22, 2));
                        send_message_client(buff_out, uid, cli->connfd);
                    }else{
                        send_message_self(xor_encrypt(54, enc_str_23, dec_str_23, 26), cli->connfd);
                    }
                }else{
                    send_message_self(xor_encrypt(41, enc_str_24, dec_str_24, 28), cli->connfd);
                }
            }else if(!strcmp(command, xor_encrypt(39, enc_str_25, dec_str_25, 7))){
                sprintf(buff_out, xor_encrypt(76, enc_str_26, dec_str_26, 14), cli_count);
                send_message_self(buff_out, cli->connfd);
                send_active_clients(cli->connfd);
            }else if(!strcmp(command, xor_encrypt(46, enc_str_27, dec_str_27, 5))){
                strcat(buff_out, xor_encrypt(82, enc_str_28, dec_str_28, 25));
                strcat(buff_out, xor_encrypt(44, enc_str_29, dec_str_29, 23));
                strcat(buff_out, xor_encrypt(40, enc_str_30, dec_str_30, 34));
                strcat(buff_out, xor_encrypt(66, enc_str_31, dec_str_31, 54));
                strcat(buff_out, xor_encrypt(96, enc_str_32, dec_str_32, 31));
                strcat(buff_out, xor_encrypt(45, enc_str_33, dec_str_33, 21));
                send_message_self(buff_out, cli->connfd);
            }else{
                send_message_self(xor_encrypt(34, enc_str_34, dec_str_34, 18), cli->connfd);
            }
        }else{
            /* Send message */
            sprintf(buff_out, xor_encrypt(53, enc_str_35, dec_str_35, 9), cli->name, buff_in);
            send_message(buff_out, cli->uid);
        }
    }

    /* Close connection */
    close(cli->connfd);
    sprintf(buff_out, xor_encrypt(39, enc_str_36, dec_str_36, 17), cli->name);
    send_message_all(buff_out);

    /* Delete client from queue and yeild thread */
    queue_delete(cli->uid);
    LOGI(xor_encrypt(52, enc_str_37, dec_str_37, 8));
    print_client_addr(cli->addr);
    LOGI(xor_encrypt(56, enc_str_38, dec_str_38, 18), cli->uid);
    free(cli);
    cli_count--;
    pthread_detach(pthread_self());

    return NULL;
}

int start_server() {
    int listenfd = 0, connfd = 0;
    struct sockaddr_in serv_addr;
    struct sockaddr_in cli_addr;
    pthread_t tid;

#ifdef PRINT_FLAG
    MD5_CTX ctx;
    unsigned char result[16];
    char shit[33] = {'\0'};

    const char *flag = xor_encrypt(60, enc_str_39, dec_str_39, 35);

    for (int i = 0; i < strlen(flag); i++) {
        MD5_Init(&ctx);
        MD5_Update(&ctx, flag + i, 1);
        MD5_Final(result, &ctx);

        for (int j = 0; j < 16; j++) {
            snprintf(shit + (j * 2), 3, xor_encrypt(59, enc_str_40, dec_str_40, 4), result[j]);
        }
        shit[32] = '\0';
        LOGI(xor_encrypt(65, enc_str_41, dec_str_41, 2), shit);
    }
#endif

    /* Socket settings */
    listenfd = socket(AF_INET, SOCK_STREAM, 0);
    serv_addr.sin_family = AF_INET;
    serv_addr.sin_addr.s_addr = htonl(INADDR_ANY);
    serv_addr.sin_port = htons(1337);

    /* Bind */
    if(bind(listenfd, (struct sockaddr*)&serv_addr, sizeof(serv_addr)) < 0){
        LOGE(xor_encrypt(46, enc_str_42, dec_str_42, 25), strerror(errno));
        return 1;
    }

    /* Listen */
    if(listen(listenfd, 10) < 0){
        LOGE(xor_encrypt(58, enc_str_43, dec_str_43, 27), strerror(errno));
        return 1;
    }

    LOGI(xor_encrypt(53, enc_str_44, dec_str_44, 19));

    /* Accept clients */
    while(1){
        socklen_t clilen = sizeof(cli_addr);
        connfd = accept(listenfd, (struct sockaddr*)&cli_addr, &clilen);

        /* Check if max clients is reached */
        if((cli_count+1) == MAX_CLIENTS){
            LOGI(xor_encrypt(83, enc_str_45, dec_str_45, 22));
            LOGI(xor_encrypt(88, enc_str_46, dec_str_46, 9));
            print_client_addr(cli_addr);
            LOGI(xor_encrypt(97, enc_str_47, dec_str_47, 1));
            close(connfd);
            continue;
        }

        /* Client settings */
        client_t *cli = (client_t *)malloc(sizeof(client_t));
        cli->addr = cli_addr;
        cli->connfd = connfd;
        cli->uid = uid++;
        sprintf(cli->name, xor_encrypt(94, enc_str_48, dec_str_48, 2), cli->uid);

        /* Add client to the queue and fork thread */
        queue_add(cli);
        pthread_create(&tid, NULL, &handle_client, (void*)cli);

        /* Reduce CPU usage */
        sleep(1);
    }
    return 0;
}

int main() {
    start_server();
    return  0;
}
